mapper.select:
SELECT * 
FROM plan_Mst 
WHERE planNum = #{planNum}
AND userId = #{userId}

SELECT * 
FROM plan_dt 
WHERE planNum = #{planNum}
AND userId = #{userId}
ORDER BY planDay, startTime ASC

mapper.update:
UPDATE plan_mst

SET planNum = #{planNum},
	planName = #{planName},
	startDate = #{startDate},
	endDate = #{endDate},
	dateCount = #{dateCount},
	eventColor = #{eventColor}

WHERE planNum = #{planNum}
AND userId = #{userId}

update.foreach:
UPDATE plan_dt 

SET planDate = #{dt.planDate}

WHERE planNum = #{dt.planNum}
AND planDay = #{dt.planDay}
AND userId = #{dt.userId}

UPDATE plan_dt
SET placeName = #{dt.placeName}, startTime = #{dt.startTime}, endTime = #{dt.endTime}, theme = #{dt.theme}, latitude = #{dt.latitude}, longitude = #{dt.longitude}, address = #{dt.address}, category = #{dt.category}, transportation = #{dt.transportation}, details = #{dt.details}, placeCount = #{dt.placeCount}
WHERE planDtNum = #{dt.planDtNum} AND userId = #{dt.userId}
