mapper.select:
SELECT * 
FROM plan_dt 
WHERE planNum = #{planNum} 
AND planDay = #{planDay}
ORDER BY startTime ASC

mapper.update:
UPDATE plan_mst
SET planNum = #{planNum}, planName = #{planName}, startDate = #{startDate}, endDate = #{endDate}, dateCount = #{dateCount}, eventColor = #{eventColor}
WHERE planNum = #{planNum}

UPDATE plan_dt 

SET planDate = #{planDate}

WHERE planNum = #{planNum}
AND planDay = #{planDay}
